            closeClient();
        }
    }

    bool initServer(const unsigned short &in_port) {
        // 初始化服务端用于监听的socket
        if ((listen_sock = socket(AF_INET, SOCK_STREAM, 0)) == -1) {
            cout << "ERROR socket init failed" << endl;
