int main() {
    int arr[] = {5, 3, 2, 4, 7, 6, 8};
    int size = sizeof(arr) / sizeof(arr[0]);

    BST bst = BST();

    for (int i = 0; i < size; ++i) {
        bst.insert_unrecursion(arr[i]);
    }
    inorderPrintTree(bst.get_root());
    printf("\n");
    for (int i = 0; i < size; ++i) {
        if (bst.search(arr[i])) {
            printf("find %d\n", arr[i]);
        } else {
            printf("not found %d\n", arr[i]);
        }
    }

    for (int i = 0; i < size; ++i) {
        bst.delete_node(arr[i]);
    }

    return 0;
}
