#include <cmath>

/*
    根据后续遍历生成完全n叉树，然后输出它的前序遍历
*/

template <class T>
class Node {
public:
    Node(T data);

    // 获取左边第一个子节点
    Node *get_first_child() {
        return first_child;
    }

    // 获取右边下一个兄弟节点
    Node *get_next_sibling() {
